import styled from "styled-components";

export const StyledCard = styled.div`
  background-color: cyan;
  width: 80%;
  height: 30%;
  margin: 1%;
  color: white;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  padding: 2%;
  border-radius: 10px;

  @media (min-width: 700px) {
    width: 35%;
  }
  @media (min-width: 850px) {
    width: 25%;
  }
  @media (min-width: 1000px) {
    width: 18%;
  }
`;
