import React from "react";
import "./App.css";
import { useState, useEffect } from "react";
import { Card } from "./components/card";
import { Input } from "./components/input";
interface People {
  name: string;
  age: string;
  hobby: string;
}
interface PeoplesList {
  peoples: People[];
  newPeople: People;
  changePeoples: (people: People) => void;
}
function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [hobby, setHobby] = useState("");
  const [people, setNewPeople] = useState<People>({} as People);
  const [peoples, setPeoples] = useState<People[]>([]);

  useEffect(() => setNewPeople({ name, age, hobby })[(name, age, hobby)]);
  const changePeoples = (name: string, age: string, hobby: string) => {
    setPeoples([...peoples, people]);
  };
  return (
    <>
      <div className="App">
        <Input
          placeholder={"nome"}
          label={"Digite um nome"}
          onChange={(e) => setName(e.target.value)}
        />
        <Input
          placeholder={"idade"}
          label={"DIgite a idade"}
          onChange={(e) => setAge(e.target.value)}
        />
        <Input
          placeholder={"hobby"}
          label={"Digite o hobby"}
          onChange={(e) => setHobby(e.target.value)}
        />
        <button onClick={() => changePeoples(name, age, hobby)}>submit</button>
      </div>
      {console.log(people, "teste2")}
      {peoples.map((people) => (
        <Card name={people.name} age={people.age} hobby={people.hobby} />
      ))}
    </>
  );
}

export default App;
