import styled from "styled-components";
export const Container = styled.article`
  max-width: 100vw;
  min-height: 100vh;
  background-color: gray;
  display: flex;
  align-items: center;
  flex-direction: column;

  div#form {
    background-color: blue;
    display: flex;
    align-items: center;
    flex-direction: column;
    border-radius: 10px;
    padding: 1%;
    margin-bottom: 2%;
  }

  div#cards {
    width: 90%;
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
  }
`;
